import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import axios from "axios";
import addressJson from "./address.json";
import "./App.css";

const { address } = addressJson;
let attempt = 1;

function start() {
  const root = ReactDOM.createRoot(document.getElementById("root"));
  root.render(
    <>
      <h1>Starting...</h1>
      <p>
        Pinging the web-server at {address}. Attempt: {attempt.toString()}
      </p>
    </>
  );
  axios
    .get(address)
    .then((res) => {
      if (res.status == 200) {
        root.render(
          <React.StrictMode>
            <App />
          </React.StrictMode>
        );
      }
    })
    .catch((error) => {
      if (attempt == 3) {
        root.render(
          <React.StrictMode>
            <p>There was an error</p>
          </React.StrictMode>
        );
      }

      attempt++;
      start();
    });
}

start();
